  #{startdate,jdbcType=TIMESTAMP}, #{endtime,jdbcType=TIMESTAMP}, #{time,jdbcType=INTEGER}, 
  #{reason}, #{picture,jdbcType=VARCHAR}, #{approver,jdbcType=BIGINT}, 
  #{note}, #{state,jdbcType=INTEGER}, #{result,jdbcType=INTEGER}, #{progress,jdbcType=INTEGER}
  )
